/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     struct ListNode *next;
 * };
 */
struct ListNode* removeElements(struct ListNode* head, int val) {
    struct ListNode* dummyHead = malloc(sizeof(struct ListNode));
    dummyHead->next = head;
    struct ListNode* p = dummyHead;
    while(p->next != NULL) {
        if (p->next->val == val) {
            p->next = p->next->next;
        }
        else {
            p = p->next;
        }
    }
    return dummyHead->next;

}
```



### [707. è®¾è®¡é“¾è¡¨](https://leetcode.cn/problems/design-linked-list/)

> ä½ å¯ä»¥é€‰æ‹©ä½¿ç”¨å•é“¾è¡¨æˆ–è€…åŒé“¾è¡¨ï¼Œè®¾è®¡å¹¶å®ç°è‡ªå·±çš„é“¾è¡¨ã€‚
>
> å•é“¾è¡¨ä¸­çš„èŠ‚ç‚¹åº”è¯¥å…·å¤‡ä¸¤ä¸ªå±æ€§ï¼š`val` å’Œ `next` ã€‚`val` æ˜¯å½“å‰èŠ‚ç‚¹çš„å€¼ï¼Œ`next` æ˜¯æŒ‡å‘ä¸‹ä¸€ä¸ªèŠ‚ç‚¹çš„æŒ‡é’ˆ/å¼•ç”¨ã€‚
>
> å¦‚æœæ˜¯åŒå‘é“¾è¡¨ï¼Œåˆ™è¿˜éœ€è¦å±æ€§ `prev` ä»¥æŒ‡ç¤ºé“¾è¡¨ä¸­çš„ä¸Šä¸€ä¸ªèŠ‚ç‚¹ã€‚å‡è®¾é“¾è¡¨ä¸­çš„æ‰€æœ‰èŠ‚ç‚¹ä¸‹æ ‡ä» **0** å¼€å§‹ã€‚
>
> å®ç° `MyLinkedList` ç±»ï¼š
>
> - `MyLinkedList()` åˆå§‹åŒ– `MyLinkedList` å¯¹è±¡ã€‚
> - `int get(int index)` è·å–é“¾è¡¨ä¸­ä¸‹æ ‡ä¸º `index` çš„èŠ‚ç‚¹çš„å€¼ã€‚å¦‚æœä¸‹æ ‡æ— æ•ˆï¼Œåˆ™è¿”å› `-1` ã€‚
> - `void addAtHead(int val)` å°†ä¸€ä¸ªå€¼ä¸º `val` çš„èŠ‚ç‚¹æ’å…¥åˆ°é“¾è¡¨ä¸­ç¬¬ä¸€ä¸ªå…ƒç´ ä¹‹å‰ã€‚åœ¨æ’å…¥å®Œæˆåï¼Œæ–°èŠ‚ç‚¹ä¼šæˆä¸ºé“¾è¡¨çš„ç¬¬ä¸€ä¸ªèŠ‚ç‚¹ã€‚
> - `void addAtTail(int val)` å°†ä¸€ä¸ªå€¼ä¸º `val` çš„èŠ‚ç‚¹è¿½åŠ åˆ°é“¾è¡¨ä¸­ä½œä¸ºé“¾è¡¨çš„æœ€åä¸€ä¸ªå…ƒç´ ã€‚
> - `void addAtIndex(int index, int val)` å°†ä¸€ä¸ªå€¼ä¸º `val` çš„èŠ‚ç‚¹æ’å…¥åˆ°é“¾è¡¨ä¸­ä¸‹æ ‡ä¸º `index` çš„èŠ‚ç‚¹ä¹‹å‰ã€‚å¦‚æœ `index` ç­‰äºé“¾è¡¨çš„é•¿åº¦ï¼Œé‚£ä¹ˆè¯¥èŠ‚ç‚¹ä¼šè¢«è¿½åŠ åˆ°é“¾è¡¨çš„æœ«å°¾ã€‚å¦‚æœ `index` æ¯”é•¿åº¦æ›´å¤§ï¼Œè¯¥èŠ‚ç‚¹å°† **ä¸ä¼šæ’å…¥** åˆ°é“¾è¡¨ä¸­ã€‚
> - `void deleteAtIndex(int index)` å¦‚æœä¸‹æ ‡æœ‰æ•ˆï¼Œåˆ™åˆ é™¤é“¾è¡¨ä¸­ä¸‹æ ‡ä¸º `index` çš„èŠ‚ç‚¹ã€‚

æ‰¾å‡†æŒ‡å‘çš„å…ƒç´ ã€‚å‰ä¸€ä¸ªè¿˜æ˜¯åä¸€ä¸ªã€‚å¦å¤–éœ€è¦åˆ†ç±»åˆ†æå¤´å°¾çš„æƒ…å†µã€‚

```c
typedef struct MyLinkedList{
    int val;
    struct MyLinkedList* next;
} MyLinkedList;


MyLinkedList* myLinkedListCreate() {
    // å®šä¹‰è™šæ‹Ÿå¤´èŠ‚ç‚¹
    MyLinkedList* head = (MyLinkedList*)malloc(sizeof(MyLinkedList));
    head->next = NULL;
    return head;
}

int myLinkedListGet(MyLinkedList* obj, int index) {
    MyLinkedList* p = obj->next; //å®šä¹‰å·¥ä½œæŒ‡é’ˆ
    for (int i = 0; p != NULL; i++) {
        if (i == index) {
            return p->val;
        }
        else {
            p = p->next;
        }
    }
    return -1;
}

void myLinkedListAddAtHead(MyLinkedList* obj, int val) {
    MyLinkedList* newHead = (MyLinkedList*)malloc(sizeof(MyLinkedList));
    newHead->next = obj->next;
    newHead->val = val;
    obj->next = newHead;
}

void myLinkedListAddAtTail(MyLinkedList* obj, int val) {
    MyLinkedList* p = obj->next; //å®šä¹‰å·¥ä½œæŒ‡é’ˆ
    MyLinkedList* newTail = (MyLinkedList*)malloc(sizeof(MyLinkedList));
    newTail->val = val;
    newTail->next = NULL;
    if (p != NULL) {
        while(p->next != NULL) {
            p = p->next;
        }
        p->next = newTail;
    }
    else {
        obj->next = newTail;
    }

}

void myLinkedListAddAtIndex(MyLinkedList* obj, int index, int val) {
    MyLinkedList* p = obj->next; //å·¥ä½œæŒ‡é’ˆ
    if (index == 0) {
        myLinkedListAddAtHead(obj, val);
    }
    MyLinkedList* newAdd = (MyLinkedList*)malloc(sizeof(MyLinkedList));
    newAdd->val = val;
    for(int i = 0; p != NULL; i++) {
        if (i == index - 1) {
            newAdd->next = p->next;
            p->next = newAdd;
            break;
        }
        else {
            p = p->next;
        }
    }
}

void myLinkedListDeleteAtIndex(MyLinkedList* obj, int index) {
    MyLinkedList* p = obj->next;
    if (index == 0){
        if (p != NULL) {
            obj->next = p->next;
        }
    }
    else {
        for (int i = 0; p->next != NULL; i++) {
            if (i == index - 1) {
                p->next = p->next->next; 
                if (p->next == NULL) {
                    break;
                }
                else {
                    p = p->next;
                }    
            }
            else {
                p = p->next;
            }
        }
    }
}

void myLinkedListFree(MyLinkedList* obj) {
    while(obj != NULL) {
        MyLinkedList* temp =  obj;
        obj = obj->next;
        free(temp);
    }
}

/**
 * Your MyLinkedList struct will be instantiated and called as such:
 * MyLinkedList* obj = myLinkedListCreate();
 * int param_1 = myLinkedListGet(obj, index);

 * myLinkedListAddAtHead(obj, val);

 * myLinkedListAddAtTail(obj, val);

 * myLinkedListAddAtIndex(obj, index, val);

 * myLinkedListDeleteAtIndex(obj, index);

 * myLinkedListFree(obj);
*/
```

