 * struct ListNode {
 *     int val;
 *     struct ListNode *next;
 * };
 */
struct ListNode *getIntersectionNode(struct ListNode *headA, struct ListNode *headB) {
    typedef struct ListNode ListNode;

    ListNode *p = headA;
    ListNode *q = headB;

    // è®¡ç®—é“¾è¡¨é•¿åº¦çš„å·®
    int lenA = 0, lenB = 0;
    int gap = 0;
    while (p != NULL) {
        lenA++;
        p = p->next;
    }
    while (q != NULL) {
        lenB++;
        q = q->next;
    }

    // ç§»åŠ¨åˆ°ç›¸åŒé•¿åº¦
    p = headA;
    q = headB;
    if (lenA > lenB) {
        gap = lenA - lenB;
        while(gap != 0) {
            gap--;
            p = p->next;
        }
    }
    else {
        gap = lenB - lenA;
        while(gap != 0) {
            gap--;
            q = q->next;
        }
    }

    // ä¸€ä¸ªä¸€ä¸ªæ¯”è¾ƒ
    while (p != NULL && q != NULL){
        if (p == q) {
            return p;
        }
        else {
            p = p->next;
            q = q->next;
        }
    }
    return NULL;

}
