            for (int j = i + 1; j < nums.size(); j++) {
                // if (nums[i] + nums[j] > target && nums[i] + nums[j] >= 0) {
                //     break; //å‰ªæå¤„ç†ï¼Œç±»ä¼¼ä¹‹å‰çš„é¦–å…ƒç´ å¤§äº0ï¼Œé‚£ä¹ˆå¿…ç„¶ä¸è¡Œ
                // }
                if (j > i+1 && nums[j] == nums[j-1]) {  // è¿™è¾¹çš„é‡ç‚¹æ˜¯j > i+1è€Œéj>0
                    continue;
                }
                // åŒæŒ‡é’ˆ
                int left = j + 1;
                int right = nums.size() - 1;
                while(left < right) {
                    if ((long) nums[i] + nums[j] + nums[left] + nums[right] < target) { //ä¼šæº¢å‡º
                        left++;
                    }
                    else if ((long) nums[i] + nums[j] + nums[left] + nums[right] > target) {
                        right--;
