```cpp
class Solution {
public:
    int countPrimes(int n) {
        vector<int> isPrime(n, 1);
        int output = 0;
        for (int i = 2; i < n; i++) {
            if (isPrime[i]) {
                output++;
                if ((long long)i * i < n) {
                    for (int j = i * i ; j < n ; j += i) {
                        isPrime[j] = 0;
                    }
                }
            }
        }
        return output;
    }
};
```



