class Solution {
public:
    void reverseString(vector<char>& s) {
        int left = 0;
        int right = s.length() - 1;
        while (left < right) {
            int temp = s[left];
            s[left] = s[right];
            s[right] = temp;
            left++;
            right--;
        }
    }
};
