```cpp
class Solution {
public:
    string reverseStr(string s, int k) {
        int loop = s.length() / (2 * k) + 1;
        for (int i = 0; i < loop; i++) {
            // å¦‚æœä¸æ˜¯æœ€åä¸€ä¸ªloopï¼Œåˆ™åè½¬å½“å‰loopå‰kä¸ª
            if (i != loop - 1) {  //å’Œä¸‹é¢last_loop >= kåˆå¹¶
                int left = 2 * i * k;
                int right = 2 * i * k + (k - 1);
                while (left < right) {
                    swap(s[left], s[right]);
                    left++;
                    right--;
                }
            }
            else {
                int last_loop = s.length() % (2 * k);
                int left = 2 * i * k;
                int right;
                if (last_loop < k) { //0çš„æƒ…å†µä¹Ÿæ²¡äº‹ï¼Œä¸ä¼šè¿›å…¥whileå¾ªç¯ã€‚
                    right = s.length() - 1;
                } 
                else {  //å’Œä¸Šé¢i != loop - 1åˆå¹¶
                    right = 2 * i * k + (k - 1);
                }
                while (left < right) {
                    swap(s[left], s[right]);
                    left++;
                    right--;
                }
            }
        }
        return s;
    }
};
```

