class Solution {
public:
    bool repeatedSubstringPattern(string s) {
        int j = 0;
        int next[s.length()];
        next[0] = 0;
        int maxLoop = 1;
        for (int i = 1; i < s.length(); i++) {
            while (j > 0 && s[j] != s[i]) {
                j = next[j - 1];
            }
            if (s[j] == s[i]) {
                j++;
            }
            next[i] = j;
            if (next[i] > maxLoop) {
                maxLoop = next[i];
            }
        }
        for (int i = 0; i < s.length(); i++) {
            cout << next[i] << ' ';
        }
        if(s.length() % (s.length() - maxLoop) == 0) {
            return true;
        }
        else{
            return false;
        }

    }
};
```

