class Solution {
public:
    bool isValid(string s) {
        if (s.length() % 2 != 0) {
            return false;
        }
        stack <char> bracks;
        for (int i  = 0; i < s.length(); i++) {
            if (s[i] == '(' || s[i] == '[' || s[i] == '{') {
                bracks.push(s[i]);
            }
            else {
                switch (s[i]) {
                    case ')':
                        if (!bracks.empty() && bracks.top() == '(') {
                            bracks.pop();
                            break;
                        }
                        else {
                            return false;
                        }
                    case ']':
                        if (!bracks.empty() && bracks.top() == '[') {
                            bracks.pop();
                            break;
                        }
                        else {
                            return false;
                        }
                    case '}':
                        if (!bracks.empty() && bracks.top() == '{') {
                            bracks.pop();
                            break;
                        }
                        else {
                            return false;
                        }
                    default:
                        return false;
                }
            }
        }
        if (bracks.size() == 0) {
            return true;
        }
        else {
            return false;
        }
    }
};
```

