class Solution {
public:
    int evalRPN(vector<string>& tokens) {
        stack <int> nums;
        int result;
        for (string token:tokens) {
            if (token == "+" ) {
                result = nums.top();
                nums.pop();
                result = nums.top() + result;
                nums.pop();
                nums.push(result);
            }
            else if (token == "-") {
                result = nums.top();
                nums.pop();
                result = nums.top() - result;
                nums.pop();
                nums.push(result);
            }
            else if (token == "*") {
                result = nums.top();
                nums.pop();
                result = nums.top() * result;
                nums.pop();
                nums.push(result);
            }
            else if (token == "/") {
                result = nums.top();
                nums.pop();
                result = nums.top() / result;
                nums.pop();
                nums.push(result);
            }
            else { // æ•°å­—
                nums.push(stoi(token));
            }
        }
        return nums.top();
    }
};
