lass Solution {
public:
    int sumOfLeftLeaves(TreeNode* root) {
        queue <TreeNode*> Q;
        int leftSum = 0;
        if (root != NULL) {
            Q.push(root);
        }
        while (!Q.empty()) {
            int qSize = Q.size();
            for (int i = 0; i < qSize; i++){
                TreeNode *p = Q.front();
                Q.pop();
                if (p->left){
                    Q.push(p->left);
                    if (!p->left->left && !p->left->right){
                        leftSum += p->left->val;
                    }
                }
                if (p->right) {
                    Q.push(p->right);
                }
            }
        }
        return leftSum;
    }
};
