class Solution {
public:
    int getMinimumDifference(TreeNode* root) {
        stack <TreeNode*> S;
        TreeNode *p = root;
        int pre = -1;
        int minDistance = INT_MAX;
        while(p || !S.empty()){
            if(p){
                S.push(p);
                p = p->left;
            }
            else {
                p = S.top();
                S.pop();
                if((pre != -1) && (p->val - pre) < minDistance) {
