    void backtracking (int n, int row) {
        if (row == n) {
            vector<string> temp(n, string(n, '.'));            
            for (int i = 0; i < n; i++) {
                // if (!(n % 2) && columns[0] == n / 2) { // å¥‡æ•°åˆ—æƒ…å†µï¼Œä¸­é—´çš„æƒ…å†µæ˜¯ä¸èƒ½åè½¬çš„
                temp[i][columns[i]] = 'Q';
