            // cout << endl;
            return;
        }
        bool flag = false; // flagæ£€æµ‹åˆ—æ ‡æ˜¯å¦å’Œå‰é¢å…ƒç´ å†²çª
        for(int column = 0; column < n; column++) { //ç¬¬rowå…ƒç´ çš„åˆ—æ ‡, ä¸€å®šæœ‰ä¸€ä¸ªåè½¬çš„æƒ…å†µæˆç«‹
            flag = false;
            for (int j = 0; j < row; j++) { // jè¡¨ç¤ºå‰é¢çš„è¡Œ
                if (columns[j] == column || (row - j) == (columns[j] - column) || (row - j) == - (columns[j] - column)) { // åˆ—æ ‡é‡åˆï¼Œæ–œçº¿
                    flag = true;
                    break;
                }
            }
            if (!flag) { // æ²¡é—®é¢˜ï¼Œå›æº¯
                columns.emplace_back(column);
                backtracking(n, row + 1);
                columns.pop_back();
            }
        }

    }

    vector<vector<string>> solveNQueens(int n) {
