class Solution {
public:
    int fib(int n) {
        if (n == 0) {
            return 0;
        }
        if (n == 1) {
            return 1;
        }
        int prev0 = 0;
        int prev1 = 1;
        int temp;
        while (n != 1) {
            temp = prev0 + prev1;
            prev0 = prev1;
            prev1 = temp;
            n--;
        }
        return temp;
    }
};
```

