class Solution {
public:
    int uniquePaths(int m, int n) {
        vector<vector<int>> roadMap (m, vector<int> (n,0));
        roadMap[0][0] = 1;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (i == 0 && j == 0) {
