class Solution {
public:
    int integerBreak(int n) {
        long long maxNum  = 0;
        for (int i = 2; i <= n; i++) {
            long long prod = 1;
            if (n % i > i - n % i){  // è¯´æ˜è¦è¿›ä½
                int factor = n / i + 1;
                for (int j = 0; j < i - 1; j++) {
                    prod *= factor;
                }
                prod *= n % i + n / i - (i - 1);
            }
            else {
                int factor = n / i;
                for (int j = 0; j < i - 1; j++) {
                    prod *= factor;
                }
                prod *= (factor + n % i);
            }
            if (maxNum < prod) {
                maxNum = prod;
            }
        }
        return maxNum;
    }
};
```

