class Solution {
public:
    vector<vector<int>> dp;
    vector<int> temp; // ç´¢å¼•0è¡¨ç¤ºä¸å·ï¼Œ ç´¢å¼•1è¡¨ç¤ºå·

    void robPostOrder (TreeNode *p) {
        if (p->left) {
            robPostOrder(p->left);
        }
        if (p->right) {
            robPostOrder(p->right);
        }

        // å½“å‰èŠ‚ç‚¹çš„å››ç§æƒ…å†µï¼šå·¦å³å­æ ‘éƒ½æœ‰ã€æœ‰ä¸€ä¸ªã€éƒ½æ²¡æœ‰
        if (p->left && p->right) {
            // è¿™é‡Œæœ‰å››ä¸ªæƒ…å†µå•Šï¼šå·¦å·å³ä¸å·ã€å·¦ä¸å·å³å·ã€å·¦å·å³å·ã€å·¦ä¸å·å³ä¸å·ã€‚å››ç§æƒ…å†µè¿™æ¬¡éƒ½å¯ä»¥ä¸å·ï¼Œä½†æ˜¯åªæœ‰æœ€åä¸€ç§æƒ…å†µè¿™æ¬¡å¯ä»¥å·
            // å½“å‰ä¸å·ï¼šé‚£å°±æ˜¯å·¦å³ç´¢å¼•ä¸Šä¸€æ¬¡çš„å·çš„å’Œï¼ˆè¿™é‡Œåº”è¯¥æ˜¯åŠ è€Œä¸æ˜¯å–æœ€å¤§å€¼ï¼‰å½“å‰ä¸å·ä¸Šä¸€æ¬¡ä¹Ÿå¯ä»¥ä¸å·å•Šï¼Œ
            temp[0] = max(dp[dp.size() - 2][1] + dp[dp.size() - 1][1], max(dp[dp.size() - 2][0] + dp[dp.size() - 1][0], max(dp[dp.size() - 2][1] + dp[dp.size() - 1][0], dp[dp.size() - 2][0] + dp[dp.size() - 1][1]))); 
            // å½“å‰å·ï¼šé‚£å°±æ˜¯å·¦å³ç´¢å¼•ä¸Šä¸€æ¬¡çš„ä¸å·çš„å’Œï¼ˆè¿™é‡Œåº”è¯¥æ˜¯åŠ è€Œä¸æ˜¯å–æœ€å¤§å€¼ï¼‰ï¼ˆç›¸å½“äºæ˜¯å½“å‰ç´¢å¼•ä¸Šä¸Šæ¬¡å¯èƒ½å·çš„å’Œï¼‰
            temp[1] = dp[dp.size() - 2][0] + dp[dp.size() - 1][0] + p->val; 
            dp.pop_back(); // éœ€è¦æŠŠå­æ ‘çš„popæ‰ï¼Œä¸ç„¶æ ¹èŠ‚ç‚¹æ‰¾ä¸åˆ°å·¦å­æ ‘çš„
            dp.pop_back();
        }
        else if (p->left || p->right) { // æœ‰ä¸€ä¸ªéç©º
            // å½“å‰ä¸å·ï¼šé‚£å°±æ˜¯éç©ºå­æ ‘ä¸Šä¸€æ¬¡çš„å·çš„å€¼
            temp[0] = max(dp[dp.size() - 1][1], dp[dp.size() - 1][0]); 
            // å½“å‰å·ï¼šé‚£å°±æ˜¯éç©ºå­æ ‘ä¸Šä¸€æ¬¡çš„ä¸å·çš„å€¼ï¼ˆç›¸å½“äºæ˜¯å½“å‰ç´¢å¼•ä¸Šä¸Šæ¬¡å¯èƒ½å·çš„å€¼ï¼‰
            temp[1] = dp[dp.size() - 1][0] + p->val; 
            dp.pop_back();
        }
        else { // æ ¹èŠ‚ç‚¹
            temp[0] = 0; // ä¸å·
            temp[1] = p->val; // å·
        }
