                S.pop(); //åªæœ‰åœ¨æ¯popä¸€æ¬¡æ‰ç›¸å½“äºç§¯æ”’äº†ä¸€éƒ¨åˆ†æ°´
                if (!S.empty()) { // Sç©ºäº†å·¦è¾¹æ˜¯æŒ¡ä¸ä½æ°´çš„
                    int length = i - S.top() - 1; // å› ä¸ºå‡å®ŒåŒ…å«äº†æœ€å·¦è¾¹æŒ¡ä½çš„æŸ±å­
                    output += length * (min(height[S.top()] , height[i]) - last); // è·ç¦»*é«˜åº¦
                }
                // cout << output << endl;
            }            
            S.push(i);
        }
