class Solution {
public:
    int dir[4][2] = {0,1,1,0,-1,0,0,-1};
    void bfs(vector<vector<char>> &board, int x, int y) {
        queue <pair<int, int>> Q;
        Q.push({x, y});
        while (!Q.empty()) {
            pair<int,int> cur = Q.front();
            Q.pop();
            for (int i = 0; i < 4; i++) {
                int xNext = cur.first + dir[i][0];
                int yNext = cur.second + dir[i][1];
                if (xNext <= 0 || yNext <= 0 || xNext >= board.size() - 1 || yNext >= board[0].size() - 1) {
                    continue;
                }
                if (board[xNext][yNext] == 'O') {
                    board[xNext][yNext] = 'T';
                    Q.push({xNext, yNext});
                }
            }
        }

    }
    void solve(vector<vector<char>>& board) {
        for (int j = 0; j < board[0].size(); j++) {// é¦–å°¾è¡Œ
            if (board[0][j] == 'O') {
                bfs(board, 0, j);
            }
            if (board[board.size() - 1][j] == 'O') {
                bfs(board, board.size() - 1, j);
            }
        }
        for (int i = 1; i < board.size() - 1; i++) {// é¦–å°¾åˆ—
            if (board[i][0] == 'O') {
                bfs(board, i, 0);
            }
            if (board[i][board[0].size() - 1] == 'O') {
                bfs(board, i, board[0].size() - 1);
            }
        }
        for (int i = 1; i < board.size() - 1; i++) {
            for (int j = 1; j < board[0].size() - 1; j++) {
                // cout << board[i][j] << " ";
                if (board[i][j] == 'O') {
                    board[i][j] = 'X';
                }
                else if (board[i][j] == 'T') {
                    board[i][j] = 'O';
                }
            }
            // cout << endl;
        }
    }
};
