    //             area += areaMap[areaIndex[xNext][yNext]];
    //             areaMap[areaIndex[xNext][yNext]] = 0; // ç½®é›¶åªèƒ½åŠ ä¸€æ¬¡ï¼ŒåŒæ—¶è¿™ä¸ªå±€éƒ¨å˜é‡ä¸å½±å“å…¨å±€
    //         }
    //     }
    //     // cout << x << " " << y << " " << area << endl;
    //     return area + 1;
    // }

    int largestIsland(vector<vector<int>>& grid) {
        // vector<vector<int>> areaIndex(grid.size(), vector<int>(grid[0].size())); // ä¿å­˜æ¯ä¸ªä½ç½®çš„ç¼–å·
        unordered_map <int, int> areaMap; // ä¿å­˜ç¼–å·å’Œé¢ç§¯çš„å…³ç³»
        bool allOne = true;

        // ä¸€éæ·±æœå…ˆæŠŠæ¯ä¸ªä½ç½®å¯¹åº”çš„å²›å±¿å¤§å°ä¿å­˜ã€‚
        int islandIndex = 2;
        for (int i = 0; i < grid.size(); i++) {
            for(int j = 0; j < grid[0].size(); j++) {
                if (grid[i][j] == 0) {
                    allOne = false;
