class Solution {
public:
    int longestConsecutive(vector<int>& nums) {
        unordered_set<int> hashSet;
        // å…ˆå»é‡
        for(auto num:nums) {
            hashSet.insert(num);
        }
        int output = 0;
        for(auto num:hashSet) {
            // è¦é˜²æ­¢åç»­çš„æ•°å­—ç»§ç»­éå†ï¼Œæ‰€ä»¥è¦ç¡®ä¿å¼€å§‹éå†ä¸€å®šæ˜¯ç»“å°¾ï¼ï¼ï¼é¢˜è§£çš„å†™æ³•æ˜¯ä¿ä¸€å®šæ˜¯å¼€å¤´
            if (hashSet.find(num + 1) == hashSet.end()) {
                int count = 1;
                int cur = num;
                while (hashSet.find(cur - 1) != hashSet.end()) {
                    // cout << num << " " << cur << " " <<  count << endl;
                    count++;
                    cur--;
                }
                output = max(output, count);
            }
        }
        return output;
    }
};
