        unordered_map<int, int> preSum;
        preSum[0] = 1;
        int output = 0;
        int pre = 0;
        for (auto num:nums) {
            pre += num;
            output += preSum[pre - k];
            preSum[pre] ++;
        }
        return output;
    }
};
