/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    bool isPalindrome(ListNode* head) {
        stack<int> S;
        ListNode *p = head;
        while (p != NULL) {
            S.push(p->val);
            p = p->next;
        }
        p = head;
        while (p != NULL) {
            if(p->val != S.top()) {
                return false;
            }
            S.pop();
            p = p->next;
        }
        return true;
    }
};
