            p = p2;
            p2 = p2->next;
        }
        ListNode *head = p;
        while (p1 != nullptr && p2 != nullptr) {
            if (p1->val >= p2->val) {
                p->next = p2;
                p = p->next;
                p2 = p2->next;
            }
            else {
                p->next = p1;
                p = p->next;
                p1 = p1->next;
            }
        }
        if (p1 == nullptr) {
            p->next = p2;
        }
        else {
            p->next = p1;
        }
        return head;
    }
};
