            return l1;
        }
        ListNode *p1 = l1;
        ListNode *p2 = l2;
        bool CF = false;
        p1->val = p1->val + p2->val;
        if(p1->val >= 10) {
            p1->val -= 10;
            CF = true;
        }
        while (p1->next != nullptr || p2->next != nullptr) {
            if (p1->next == nullptr) {
                p1->next = new ListNode(0);
            }
            else if (p2->next == nullptr) {
                p2->next = new ListNode(0);
            }
            p1 = p1->next;
            p2 = p2->next;
            p1->val = p1->val + p2->val;
            if (CF) {
                p1->val += 1;
            }
            if(p1->val >= 10) {
                p1->val -= 10;
                CF = true;
            }
            else {
                CF = false;
            }
        }
        if (CF) {
            p1->next = new ListNode(1);
        }
        return l1;
    }
};
