class Solution {
public:

    ListNode* sortList(ListNode* head) {
        return sortTwoList (head, nullptr);
    }

    ListNode* sortTwoList(ListNode* head, ListNode* tail) {
        if (head == nullptr) { // é“¾è¡¨ä¸ºç©º
            return head;
        }
        if (head->next == tail) { // åªå‰©ä¸€ä¸ªå…ƒç´ 
            head->next = nullptr;
            return head;
        }
        ListNode* slow = head;
        ListNode* fast = head;
        while (fast != tail) {
            slow = slow->next;
            fast = fast->next;
            if (fast != tail) {
                fast = fast->next;
            }
        }
        ListNode* mid = slow;
        return mergeTwoList(sortTwoList(head, mid), sortTwoList(mid, tail));
    }

    ListNode *mergeTwoList(ListNode* head1, ListNode* head2) {
        if (head1 == nullptr) {
            return head2;
        }
        else if (head2 == nullptr) {
            return head1;
        }
        ListNode *p1 = head1;
        ListNode *p2 = head2;

        ListNode *p;
        if (head1->val < head2->val) {
            p = p1;
            p1 = p1->next;
        }
        else {
            p = p2;
            p2 = p2->next;
        }
        ListNode * head = p;
        while (p1 != nullptr && p2 != nullptr) {
            if (p1->val < p2->val) {
                p->next = p1;
                p = p->next;
                p1 = p1->next;
            }
            else{
                p->next = p2;
                p = p->next;
                p2 = p2->next;
            }
        }
        if (p1 == nullptr) {
            p->next = p2;
        }
        else {
            p->next = p1;
        }
        return head;
    }
};
