class Solution {
public:
    int dir[4][2] = {0,1,1,0,-1,0,0,-1};
    int bfs (vector<vector<int>>& grid, int x, int y) {
        int time = 0;
        queue<pair<int, int>> Q;
        vector<vector<bool>> visited(grid.size(), vector<bool>(grid[0].size(), false));
        visited[x][y] = true;
        Q.push({x, y});

        while (!Q.empty()) {
            int currLoopNum = Q.size();
            for (int i = 0; i < currLoopNum; i++) {
                pair<int, int> curr = Q.front();
                Q.pop();
                int xCurr = curr.first;
                int yCurr = curr.second;
                for (int i = 0; i < 4; i++) {
                    int xNext = xCurr + dir[i][0];
                    int yNext = yCurr + dir[i][1];
                    if (xNext < 0 || yNext < 0 || xNext >= grid.size() || yNext >= grid[0].size() || grid[xNext][yNext] == 2) { // 2è¯´æ˜å°±æ˜¯ä¸­å¿ƒçƒ‚æ©˜å­ï¼Œä¸éœ€è¦é‡æ–°è®¡ç®—
                        continue;
                    }
                    if ((grid[xNext][yNext] == 1 || grid[xNext][yNext] == 3) && visited[xNext][yNext] == false) {
                        Q.push({xNext, yNext});
                        visited[xNext][yNext] = true;
                        grid[xNext][yNext] = 3;
                        // cout << xNext << " " << yNext;
                    }
                }
            }
            time ++;
            // cout << time << endl;
        }
        // cout << "A";
        return time - 1;
    }

    int orangesRotting(vector<vector<int>>& grid) {
        int row = grid.size();
        int column = grid[0].size();
        int time = 0;
        queue<pair<int, int>> Q;
        bool flagOne = false;

        for (int i = 0; i < row; i++) {
            for (int j = 0; j < column; j++) {
                if (grid[i][j] == 2) {
                    Q.push({i, j});
                }
                if (grid[i][j] == 1) {
                    flagOne = true;
                }
            }
        }
        if (Q.size() == 0 && flagOne == false) { // è€ƒè™‘æ²¡æœ‰çƒ‚æ©˜å­
            return 0;
        }

        while (!Q.empty()) {
            int currLoopNum = Q.size();
            for (int i = 0; i < currLoopNum; i++) {
                pair<int, int> curr = Q.front();
                Q.pop();
                int xCurr = curr.first;
                int yCurr = curr.second;
                for (int i = 0; i < 4; i++) {
                    int xNext = xCurr + dir[i][0];
                    int yNext = yCurr + dir[i][1];
                    if (xNext < 0 || yNext < 0 || xNext >= grid.size() || yNext >= grid[0].size() || grid[xNext][yNext] == 2) { // 2è¯´æ˜å°±æ˜¯ä¸­å¿ƒçƒ‚æ©˜å­ï¼Œä¸éœ€è¦é‡æ–°è®¡ç®—
                        continue;
                    }
                    if (grid[xNext][yNext] == 1 ) {
                        Q.push({xNext, yNext});
                        grid[xNext][yNext] = 2;
                        // cout << xNext << " " << yNext;
                    }
                }
            }
            time ++;
            // cout << time << endl;
        }

        for (int i = 0; i < row; i++) {
            for (int j = 0; j < column; j++) {
