class Solution {
public:
    vector<string> output;
    string temp;

    void backtracking(int n, int usedLeft, int usedRight) {
        if (usedLeft == n) {
            for (int i = 0; i < n - usedRight; i++) { // è¡¥é½å³æ‹¬å·
                temp.push_back(')');
            }
            output.emplace_back(temp);
            for (int i = 0; i < n - usedRight; i++) { // ä¸è¦å¿˜è®°è¿™è¾¹è¦popæ‰
                temp.pop_back();
            }
            return;
        }

        if (usedLeft < n) {
            // é€‰æ‹©åŠ å…¥â€˜(â€™ï¼Œ ä¸ç®¡ä»€ä¹ˆæƒ…å†µéƒ½å¯ä»¥ï¼Œåªè¦è¿˜æœ‰n
            temp.push_back('(');
            backtracking(n, usedLeft + 1, usedRight);
            temp.pop_back();
        }
        if (usedLeft > usedRight) {
                temp.push_back(')');
                backtracking(n, usedLeft, usedRight + 1);
                temp.pop_back();
            }
    }

    vector<string> generateParenthesis(int n) {
        backtracking(n, 0, 0);
        return output;
    }
};
```

