class Solution {
public:
    int search(vector<int>& nums, int target) {
        // å…ˆæ‰¾åˆ†ç•Œç‚¹
        int min = 0;
        int left = 1;
        int right = nums.size() - 1;
        while (left <= right) {
            int mid = (left + right) / 2;
            if (nums[0] < nums[mid]) {
                left = mid + 1;
            }
            else {
                right = mid - 1;
                min = mid;
            }
        }
        left = min;
        right = left + nums.size() - 1;
        while (left <= right) {
            int mid = (left + right) / 2;
            int i = mid % nums.size();
            if (target < nums[i]) right = mid - 1;
            else if (target > nums[i]) left = mid + 1;
            else return i;
        }
        return -1;
    }
};
