class MinStack {
public:
    stack<int> S;
    stack<int> minS;

    MinStack() {
        minS.push(INT_MAX);
    }

    void push(int val) {
        S.push(val);
        if (val < minS.top()) {
            minS.push(val);
        }
        else {
            minS.push(minS.top());
        }
    }

    void pop() {
        S.pop();
        minS.pop();
    }

    int top() {
        return S.top();
    }

    int getMin() {
        return minS.top();
    }
};

