class Solution {
public:
    int maxProduct(vector<int>& nums) {
        vector<int> dp(nums.size()); // åŒ…å«è¯¥æ•°çš„æœ€é•¿å­æ•°ç»„ä¹˜ç§¯
        dp[0] = nums[0];
        int output = nums[0];
        for (int i = 1;  i < nums.size(); i++) {
            dp[i] = nums[i];
            int temp = 1;
            for (int j = i; j >= 0; j--) {
                temp *= nums[j];
                dp[i] = max(dp[i], temp);
                if (temp == 0) {
                    break;
                }
            }
            if (dp[i] > output) {
                output = dp[i];
            }
        }
        return output;
    }
};
