class Solution {
public:
    void nextPermutation(vector<int>& nums) {
        if (nums.size() <= 1) {
            return;
        }
        int i = nums.size() - 2;
        // æ‰¾åˆ°ä»åå¾€å‰çš„ç¬¬ä¸€ä¸ªç›¸é‚»å‡åºå¯¹
        while (i >=0 && nums[i] >= nums[i+1])  {
            i--;
        }

        if (i >= 0) {// éæœ€åä¸€ä¸ªæ’åˆ—
            //æ‰¾åˆ°å°½å¯èƒ½å°çš„ã€Œå¤§æ•°ã€
            int k = nums.size() - 1;
            while (k >= 0 && nums[i] >= nums[k]) {
                k--;
            }
            swap(nums[i], nums[k]);
        }
        reverse(nums.begin() + i + 1, nums.end());
    }
};
