class Solution {
public:
    vector<int> spiralOrder(vector<vector<int>>& matrix) {
        vector<int> output;
        int m = matrix.size();
        int n = matrix[0].size();
        int length = m * n;
        int x = 0;
        int y = 0;
        int count = n;
        int dir[4][2] = {0,1,1,0,0,-1,-1,0}; // 01å³ï¼›10ä¸‹ï¼›0-1å·¦ï¼›-10ä¸Š
        int countDir = 0;

        for (int i = 0; i < length; i++) {
            output.emplace_back(matrix[x][y]);
            // cout << count << " || ";
            if(count <= 1) { // count = 1 æ¢æ–¹å‘
                countDir = (countDir + 1) % 4;
                m -= abs(dir[countDir][0]);
                n -= abs(dir[countDir][1]);
                count = abs(dir[countDir][0]) * m + abs(dir[countDir][1]) * n + 1;
            }
            // cout << i <<" "  <<count << " || " << x << " " << y << " || " << countDir << " " << dir[countDir][0] << " " << dir[countDir][1] << endl;
            x += dir[countDir][0];
            y += dir[countDir][1];
            count --;
        }
        return output;
    }
};
