class MyStack {
public:
    queue <int> queue1;
    queue <int> queue2;
    MyStack() {

    }

    void push(int x) {
        queue1.push(x);
    }

    int pop() {
        while (queue1.size() > 1) {
            queue2.push(queue1.front());
            queue1.pop();
        }
        int output = queue1.front();
        queue1.pop();
        swap(queue1, queue2);
        return output;
    }

    int top() {
        return queue1.back();
    }

    bool empty() {
        if (queue1.size() == 0) {
            return true;
        }
        else {
            return false;
        }
    }
};

