    vector<string> output;
    string temp;
    vector<string> alphabet = {"", "", "abc", "def", "ghi", "jkl", "mno", "pqrs", "tuv", "wxyz"};
    void backtracking(string digits, int start) {
        if (start == digits.length()) {
