            if (weight[i] > j) { // 这个物品比目前能承载的最大重量还要大
                dp[i][j] = dp[i - 1][j];
            }
            else {
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - weight[i]] + value[i]);
                // 这边的j - weight[i]是不会超范围的，因为他不是在前一个基础上加，而是直接找过去到前面的
            }
        }
    }
    cout << dp[itemNum - 1][maxWeight] << endl;
    return 0;
}
