class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        stack <int> S; // 保存下标；
        heights.insert(heights.begin(), 0);
        heights.push_back(0);
        S.push(0);
        int output = 0;
        int temp = 0;
