        return u == parent[u] ? u : (parent[u] = findParent(parent[u]));
    }
    bool isSame (int u, int v) {
        u = findParent(u);
        v = findParent(v);
        return u == v;
    }
    void join (int u, int v) {
        u = findParent(u);
        v = findParent(v);
        // u是v的根节点；
        parent[v] = u;
    }

    bool isTreeRemove (vector<vector<int>> &edges, vector<int> removeEdge) {
        init();
        for (auto edge:edges) {
            // if (edge[0] == removeEdge[0] && edge[1] == removeEdge[1]) {
            if(edge == removeEdge) {
                continue;
            }
            if (isSame(edge[0], edge[1])) {
                return false;
            }
            join(edge[0], edge[1]);
        }
        return true;
    }

    vector<int> getRemove (vector<vector<int>> &edges) {
        init();
        for (auto edge:edges) {
            if (isSame(edge[0], edge[1])) {
                return edge;
            }
            join(edge[0], edge[1]);
        }
        return {0, 0};
    }

    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int inDegree[1001];
        stack <vector<int>> S; // 保存入度大于2的节点
        for (auto edge:edges) {
            inDegree[edge[1]] ++;          
        }
        for (auto edge:edges) {
            if (inDegree[edge[1]] == 2) {
                S.push(edge);
            }

        }
        // 存在入度为2的节点
        if (!S.empty()) {
            if (isTreeRemove(edges, S.top())) {
                return S.top();
            }
            else {
                S.pop();
                return S.top();
            }
        }
        // 存在有向环
        return getRemove(edges);
    }
