```cpp
class Solution {
public:
    void moveZeroes(vector<int>& nums) {
        int left = 0;
        int right = 0;
        for (int right = 0; right < nums.size(); right++) {
            if (nums[right] != 0 ) {
                nums[left++] = nums[right];
            }
        }
        for (right = 0; left < nums.size(); left++) {
            nums[left] = 0;
        }
    }
};
