考虑和300题类似的思路，每次回退计算。但是超时

```cpp
class Solution {
public:
    int maxProduct(vector<int>& nums) {
        vector<int> dp(nums.size()); // 包含该数的最长子数组乘积
        dp[0] = nums[0];
        int output = nums[0];
        for (int i = 1;  i < nums.size(); i++) {
            dp[i] = nums[i];
            int temp = 1;
            for (int j = i; j >= 0; j--) {
                temp *= nums[j];
                dp[i] = max(dp[i], temp);
                if (temp == 0) {
                    break;
                }
            }
            if (dp[i] > output) {
                output = dp[i];
            }
        }
        return output;
    }
};
