   class Solution {
   public:
       int maxProfit(int k, vector<int>& prices) {
           vector buy(k+1, INT_MIN), sel(k+1, 0);
           for (int i : prices) {
               for (int j = 1; j < k+1; j++) {
                   buy[j] = max(buy[j], sel[j - 1] - i);
                   sel[j] = max(sel[j], buy[j] + i);
               }
           }
           return sel[k];
       }
